import React from 'react'
import ReactDOM from 'react-dom'

const Courses = ({ courses }) => {
  const parsed = courses.map((course) => <Course course={course} />)
  return (
    <>
      {parsed}
    </>
  )
}

const Course = ({ course }) => {
  const exercises = course.parts.map(part => part.exercises).reduce((a, b) => a + b)
  const parts = course.parts?.map(
    (part) => <p key={part.id}>{part.name} {part.exercises}</p>
  );
  return (
    <>
      <h2>{course.name}</h2>
      <div>{parts}</div>
      {<strong>Total of exercises: {exercises}</strong>}
    </>
  )
}

const App = () => {
  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    },
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]

  return (
    <>
      <h1>Web Development Curriculum</h1>
      <Courses courses={courses} />
    </>
  )
}

ReactDOM.render(
  <App />,
  document.getElementById('root')
)
