import React from 'react'
import ReactDOM from 'react-dom'

const Course = ({ course }) => {
  let parts = course.parts?.map(
    (part) => <p key={part.id}>{part.name} {part.exercises}</p>
  );
  return (
    <>
      <h1>{course.name}</h1>
      <div>{parts}</div>
    </>
  )
}

