import React from 'react'
import ReactDOM from 'react-dom'

const Course = ({ course }) => {
  let parts = course.parts?.map(
    (part) => <p key={part.id}>{part.name} {part.exercises}</p>
  );
  return (
    <>
      <h1>{course.name}</h1>
      <div>{parts}</div>
    </>
  )
}

const App = () => {
  const course = {
    id: 1,
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
        id: 2
      },
      {
        name: 'State of a component',
        exercises: 14,
        id: 3
      },
      {
        name: 'Extra part',
        exercises: 4,
        id: 4
      },
    ]
  }

  return (
    <>
      <Course course={course} />
      <strong>Total of exercises: {course.parts.reduce((pr, ne) => pr += ne)}</strong>
    </>
  )
}

ReactDOM.render(
  <App />,
  document.getElementById('root')
)
