import React, { useState } from 'react'
import Person from './components/Person'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas' }

  ])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')

  const addPerson = (event) => {
    if (persons.find(person => person.name === newName)) {
      alert(`${newName} already exist, try another.`)
    } else {
      alert('into else statement')
      event.preventDefault();
      const newPerson = {
        name: newName
      }
      setPersons(persons.concat(newPerson))
      setNewName('')
    }
  }

  const handleNameChange = (event) => {
    setNewName(event.target.value);
  }

  const handleNumberChange = (event) => {
    if (event.target.value.length <= 12) { /* 10 cifras y dos guiones */
      var cleaned = ("" + event.target.value).replace(/\D/g, "");

      let normValue = `${cleaned.substring(0, 3)}${
        cleaned.length > 3 ? "-" : ""
      }${cleaned.substring(3, 6)}${
        cleaned.length > 6 ? "-" : ""
      }${cleaned.substring(6, 11)}`;

      setNewNumber(normValue);
    }
  }

  return (
    <>
      <h2>Phonebook</h2>
      <form onSubmit={addPerson}>
        <div>
          name: <input
            value={newName}
            onChange={handleNameChange}
          />
        </div>
        <div>
          number: <input
            value={newNumber}
            onChange={handleNumberChange}
          />
        </div>
        <div>
          <button type="submit">Add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <ul>
        {persons.map(person =>
          <Person key={person.name} person={person} />
        )}
      </ul>
    </>
  )
}

export default App
