import React, { useState, useEffect } from 'react'
import axios from 'axios'
import Filter from './components/Filter'
import Countries from './components/Countries'

const api_key = process.env.REACT_APP_API_KEY
console.log(api_key); 

const App = () => {
  const [countries, setCountries] = useState([])
  const [filter, setFilter] = useState('')
  console.log(`http://api.weatherstack.com/current?access_key=${api_key}&query=New%20York`);

  useEffect(() => {
    axios 
      .get('https://restcountries.eu/rest/v2/all')
      .then(response => {
        setCountries(response.data)
      })
/*     axios
      .get(`http://http://api.weatherstack.com/current?access_key=${api_key}&query=Spain`)
      .then(response => {
        console.log(response.data);
      }) */
  }, [])

  const showCountries = () => {
    return countries.filter(country =>
      country.name.toLowerCase().includes(filter.toLowerCase()))
  }


  return (
    <>
      <div>
        find countries &nbsp;
        <Filter value={filter} onChange={event => setFilter(event.target.value)} />
        <Countries countries={showCountries()} setFilter={(newFilter) => setFilter(newFilter)} />
      </div>
    </>
  )
}

export default App
